import { Component, OnInit } from '@angular/core';

import { Router } from '@angular/router';
import { FormGroup, FormControl, AbstractControl,Validators, FormBuilder }  from '@angular/forms';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent implements OnInit{

  loginForm:any;
  submitted = false;
  show = false;
  password: string="";



constructor(private router:Router,private formBuilder:FormBuilder){


  this.loginForm = this.formBuilder.group({
    username: ['',[Validators.required,Validators.minLength(5),Validators.maxLength(20)]],

   password: ['',[Validators.required,Validators.minLength(6),Validators.maxLength(40)]]
    });
}


  ngOnInit(){

  }


  login(){
   this.submitted= true;

    this.router.navigateByUrl('/admin/dashboard');
    if (this.loginForm)
  }

  onSubmit(){

  }

  get f() {
    return this.loginForm.controls;
  }
// <!-- Switching method -->
 showhide() {
    if (this.password === 'password') {
      this.password = 'text';
      this.show = true;
    } else {
      this.password = 'password';
      this.show = false;
    }
  }

}
<div class="container-fluid  p-0">
    <div class="logindiv px-2">
        <div class="row">




            <div class="col-sm-6 emptyleft ">

            </div>
            <div class="col-sm-6 loginright ">
                <div class="col-sm-12 col-lg-2 col-md-1"></div>
                <div class="col-sm-12 col-lg-8 col-md-10">
                    <div class="card text-center">
                        <div class="text-center mb-3">
                            <img class="logo" src="assets\images\logo_sealed.png" alt="logo_sealed">
                        </div>

                        <div class="card-body logincard ">
                            <form [formGroup]="loginForm" (ngsubmit)="onSubmit()">
                            <div class="d-block"style="padding:1rem;"></div>
                            <div class="row px-3">


                                <div class="col-12 mb-4">
                                    <div class="input-group user">
                                        <span class="loginicon"> <i class="fa fa-user fa-lg"></i></span>
                                        <input type="text" formControlName='username' class="form-control" placeholder="UserName"
                                        [ngClass]="{'is-invalid':submitted && f['username'].errors}">

                                    </div>
                                    <div *ngIf="submitted && f['username'].errors" class="invalid-feedback ml-2">
                                        <div *ngIf="f['username'].errors['required']"> username is required</div>
                                        <div *ngIf="f['username'].errors['minlength']">
                                            username must be at least 5 characters
                                        </div>
                                        <div *ngIf="f['username'].errors['maxlength']">
                                            username must not exceed 20 characters
                                        </div>
                                    </div>
                                </div>

                                <div class="col-12 mb-4">
                                    <div class="input-group">
                                        <span class="loginicon"> <i class="fa fa-lock fa-lg"></i></span>
                                        <input type="password" formControlName='password' class="form-control"
                                            placeholder="Password">

                                        <div class="loginicon input-group-append"
                                            style="border-radius: 0px 8px 8px 0px;">
                                            <button
                                                style="border: none;color: #B3B2B2;font-size: 12px;">
                                                <!-- <i class="fa fa-eye" aria-hidden="true" *ngIf="show"></i> -->
                                                <i class="fa fa-eye-slash" aria-hidden="false" ></i>
                                            </button>
                                        </div>
                                    </div>
                                    <div *ngIf="submitted && f['password'].errors" class="invalid-feedback ml-2">
                                        <div *ngIf="f['password'].errors['required']">Password is required</div>
                                        <div *ngIf="f['password'].errors['minlength']">
                                            Password must be at least 6 characters
                                        </div>
                                        <div *ngIf="f['password'].errors['maxlength']">
                                            Username must not exceed 40 characters
                                        </div>
                                    </div>
                                </div>

                                <div class="col-12 mb-4">
                                    <div class="text-center">
                                        <div class=" mt-2">

                                            <button class="login" (click)="login()">
                                                LOGIN</button>


                                        </div>
                                    </div>

                                </div>
                            </div>

                        </form>


                        </div>
                    </div>
                </div>
                <div class="col-sm-12 col-lg-2 col-md-1"></div>

            </div>
        </div>
    </div>
</div>
