import { Component } from '@angular/core';
import { Router } from '@angular/router';
declare var $:any;


@Component({
  selector: 'app-admin-header',
  templateUrl: './admin-header.component.html',
  styleUrls: ['./admin-header.component.scss']
})
export class AdminHeaderComponent {

  constructor(private router:Router){



  }
  ngOnInit():void{
      var obj=this;


      $(document).ready(function() {

        $('.menu-icon').click(function() {
            $(obj).parent().toggleClass('is-tapped');
            $('#hamburger').toggleClass('open');
        });


        $('.touch .sitenavigation li > a').on('touchend',
            function(e:any) {
                if ($('.menu-icon').is(':hidden')) {
                    var parent = $(obj).parent();
                    $(obj).find('.clicked').removeClass('clicked');
                    if (parent.hasClass('clicked')) {
                        window.location.href = $(obj).attr('href');
                    } else {
                        $(obj).addClass('linkclicked');

                        // close other open menus at this level
                        $(obj).parent().parent().find('.clicked').removeClass('clicked');

                        parent.addClass('clicked');
                        e.preventDefault();
                    }
                }
            });


        $('.sitenavigation li').click(
            function(event:any) {
                if (event.stopPropagation) {
                    event.stopPropagation();
                } else {
                    event.cancelBubble = true;
                }

                if ($('.menu-icon').is(':visible')) {
                    $(obj).find('> ul').toggle();
                    $(obj).toggleClass('expanded');
                }
            }
        );


        $('.sitenavigation a, .sitenavigation li a').click(
            function(event:any) {
                if (event.stopPropagation) {
                    event.stopPropagation();
                } else {
                    event.cancelBubble = true;
                }
            }
        );


        $('.no-touch .sitenavigation li').hover(
            function() {
                if (!$('.menu-icon').is(':visible')) {
                    $(obj).find('> ul').fadeIn(100);
                }
            },
            function() {
                if (!$('.menu-icon').is(':visible')) {
                    $(obj).find('> ul').fadeOut(100);
                }
            }
        );
        });
  }
}
