  ]
  constructor(private dialog:MatDialog) { }

  ngOnInit(): void {
  }
  searchFood(e:any){
    this.offset = 0;
    this.value = e?.target?.value;
    // this.currentPage=0;
    // this.getB2bUserList();
  }
  addFood(){
     const dialogRef = this.dialog.open(FoodPopupComponent, {
        width: '650px',
        // height: item ?'400px' : '500px',
        height:'500px',
        disableClose: true,
        // data: item
  
      })
      // dialogRef.afterClosed().subscribe(() => {
      //   this.getB2bUserList()
      // });
     
  }
}
